DROP TABLE IF EXISTS Quiz;CREATE TABLE IF NOT EXISTS Quiz(username VARCHAR(20), quiz_id varchar(10), number_of_questions int default 3, PRIMARY KEY(quiz_id));CREATE INDEX IF NOT EXISTS quiz_id_index on Quiz(quiz_id);CREATE INDEX IF NOT EXISTS username_index on Quiz(username);DROP TABLE IF EXISTS Questions;CREATE TABLE IF NOT EXISTS Questions(question_id bigint auto_increment, question_text varchar(256), answer boolean, primary key(question_id));CREATE INDEX IF NOT EXISTS question_id_index on Questions(question_id);Drop TABLE IF EXISTS Score;CREATE TABLE IF NOT EXISTS Score(score_id bigint auto_increment, quiz_id varchar(10), correct_answers int, incorrect_answers int, FOREIGN KEY(quiz_id) REFERENCES Quiz(quiz_id), UNIQUE KEY quiz_id_constraint(quiz_id), PRIMARY KEY(score_id));Drop TABLE IF EXISTS QuizStatus;CREATE TABLE IF NOT EXISTS QuizStatus(quiz_status_id bigint auto_increment, quiz_id varchar(10), questions_asked int default 0, FOREIGN KEY(quiz_id) REFERENCES Quiz(quiz_id), PRIMARY KEY(quiz_status_id));Drop TABLE IF EXISTS QuizQuestions;CREATE TABLE IF NOT EXISTS QuizQuestions(id bigint auto_increment, quiz_id varchar(10), question_id bigint, FOREIGN KEY(quiz_id) REFERENCES Quiz(quiz_id), FOREIGN KEY(question_id) REFERENCES Questions(question_id));