CREATE TABLE IF NOT EXISTS Quiz(username VARCHAR(20), quiz_id varchar(10), number_of_questions int default 3, PRIMARY KEY(quiz_id));CREATE INDEX IF NOT EXISTS quiz_id_index on Quiz(quiz_id);CREATE INDEX IF NOT EXISTS username_index on Quiz(username);CREATE TABLE IF NOT EXISTS QUESTIONS(question_id bigint, question_text varchar(256), answer boolean, primary key(question_id));CREATE INDEX IF NOT EXISTS question_id_index on Questions(question_id);CREATE TABLE IF NOT EXISTS Score(score_id bigint, quiz_id bigint, correct_answers int, incorrect_answers int, FOREIGN KEY(quiz_id) REFERENCES Quiz(quiz_id), PRIMARY KEY(score_id));CREATE TABLE IF NOT EXISTS QuizStatus(quiz_status_id bigint auto_increment, quiz_id varchar(10), questions_asked int default 0, FOREIGN KEY(quiz_id) REFERENCES Quiz(quiz_id), PRIMARY KEY(quiz_status_id));CREATE TABLE IF NOT EXISTS QuizQuestions(id bigint auto_increment, quiz_id varchar(10), question_id bigint, FOREIGN KEY(quiz_id) REFERENCES Quiz(quiz_id), FOREIGN KEY(question_id) REFERENCES Questions(question_id));